import { useState, useRef, useEffect } from "react";
import { Image } from "react-bootstrap";
import "react-slideshow-image/dist/styles.css";
import "./Main.scss";

function Main() {
  const [mainBanners, setMainBanners] = useState([]);
  useEffect(() => {
    fetch("/data/mainbanner.json")
      .then((res) => res.json())
      .then((res) => {
        setMainBanners(res);
      });
  }, []);

  return (
    <div className="container">
      <div className="mainContainer">
        <Image className="mainBox" src="image/main_banner_1.png" />
        {mainBanners &&
          mainBanners.map((item) => (
            <MainBanner
              item={item}
              key={item.id}
              setCartItems={setMainBanners}
              cartItems={setMainBanners}
            />
          ))}
        <div className="news">
          <div>news</div>
          <div>news</div>
          <div>news</div>
          <div>news</div>
          <div>news</div>
        </div>

        <div className="new">
          <div>new</div>
          <div>new</div>
          <div>new</div>
          <div>new</div>
          <div>new</div>
          <div>new</div>
        </div>

        <div className="event">
          <div>event</div>
          <div>event</div>
          <div>event</div>
          <div>event</div>
          <div>event</div>
          <div>event</div>
        </div>
      </div>
    </div>
  );
}

export default Main;
