import { Image } from "react-bootstrap";
import "react-slideshow-image/dist/styles.css";
import "./Main.scss";

const [mainBanner, setMainBanner] = useState([]);
useEffect(() => {
  fetch("/data/mainbanner.json")
    .then((res) => res.json())
    .then((res) => {
      setMainBanner(res);
    });
}, []);

